import {useState} from 'react'

function Nav(props) {

    //NAV HAMBURGER
    const [toggleHamClass, setHamClass] = useState(false);

    //NAV
    const [displayNav, setDisplayNav] = useState("");
    const [navClass, setNavClass] = useState("navContainer fadeout");

    function toggleNav() {
        if (toggleHamClass === true) {
          setHamClass(false);
          setNavClass("navContainer fadeout")
          setTimeout(() => {
            setDisplayNav(false);
          }, 750);
        } else {
          setHamClass(true);
          setDisplayNav(true);
          setTimeout(() => {
            setNavClass("navContainer fadein");
          }, 10)
        }
    }

    return (
        <div className={props.navContClass}>
            <div className="navInfoContainer">
                <i className="fas fa-info-square"></i>
                <i className="fal fa-sign-in"></i>
                <i className="fas fa-question-square"></i>
            </div>
           <div className="navSocialContainer">
                <i className="fab fa-linkedin"></i>
                <i className="fal fa-envelope"></i>
                <i className="fab fa-instagram-square"></i>
           </div>
        </div>
    )
}

export default Nav;
