import Head from 'next/head';
import Nav from '../components/Nav'; 
import Hamburger from '../components/Hamburger';
import navStyles from './Nav.module.css';
import {useState} from 'react'

function Layout(props) {
    //NAV HAMBURGER
    const [toggleHamClass, setHamClass] = useState(false);

    //NAV
    const [displayNav, setDisplayNav] = useState("");
    const [navClass, setNavClass] = useState(`fadein ${navStyles.navContainer}`);

    function toggleNav() {
        if (toggleHamClass === true) {
          setHamClass(false);
          setNavClass("navContainer fadeout")
          setTimeout(() => {
            setDisplayNav(false);
          }, 750);
        } else {
          setHamClass(true);
          setDisplayNav(true);
          setTimeout(() => {
            setNavClass("navContainer fadein");
          }, 10)
        }
    }

    return (
        <div>
            <Head>
                <meta charSet="utf-8" />
                <link rel="icon" href="%PUBLIC_URL%/favicon.ico" />
                <meta name="viewport" content="width=device-width, initial-scale=1" />
                <link rel="apple-touch-icon" href="%PUBLIC_URL%/logo192.png" />
                <title>forgerson.DEV</title>
                <link href="https://fonts.googleapis.com/css2?family=Nunito&display=swap" rel="stylesheet"/>

                <script src="https://kit.fontawesome.com/03e982c024.js" crossorigin="anonymous"></script>
            </Head>
            <div>
                {props.children}
            </div>
            <Nav navContClass={navStyles.navContainer}/>
            <Hamburger />
        </div>
    );
}

export default Layout;
