import Head from 'next/head';
import Nav from '../components/Nav'; 
import Hamburger from '../components/Hamburger';
import hamStyles from '../components/Hamburger.module.css';
import {useState} from 'react'

function Layout(props) {
    //NAV HAMBURGER
    const [toggleHamClass, setHamClass] = useState(false);

    //NAV
    const [displayNav, setDisplayNav] = useState(false);
    const [navStyle, setNavStyle] = useState("");
    const [openNav, setOpenNav] = useState(false);

    function toggleNav() {
        if (toggleHamClass === true) {
          setHamClass(false);
          setDisplayNav(false);
        } else {
          setHamClass(true);
          setDisplayNav(true);
          setTimeout(() => {
            setNavClass("navContainer fadein");
          }, 10)
        }
    }

    function navBtnClick() {
      setOpenNav(true);
    }


    return (
        <div className="layout">
            <div>
                {props.children}
            </div>
            <Nav />
            <Hamburger 
              navIcon4={hamStyles.navIcon4}
              hamContainer={hamStyles.hamContainer}
              hamClass={openNav}
              hamClick={() => navBtnClick()}
              hamClass={openNav ? hamStyles.open : ""}
            />
        </div>
    );
}

export default Layout;
